{
    "parentId": "AC1ABDCBK050BA4863S9964E3DC378247DAD",
"name": "Live2D Motion Selection",
    "windowTitle": "$$C993387884B63643221AC4709B986C0A7ADE",
    "type": "custom_view",
    "windowSize": {
        "w": 800, "h": 400
    },
    "bindings": [
        { "source": "local.data.resource", "asDataFromFolder": "Live2D", "to": "positionView", "target": "model" }
    ],
    "windowSizeFormula": "var res = GS.GAME.previewResolution(); return { w: res.w + 385  + GS.CONST.WINDOW_PADDING }",
    "columns": [400, -1],
    "sections": [
        {
            "name": "Position",
            "identifier": "positionSection",
            "noBorder": true,
            "column": 0,
            "items": [
                {
                    "type": "GSLabel",
                    "text": "$$79C3606A31A6A347B80AD628C0099CFE1421",
                    "frame": [0, 0],
                    "group": "labels"
                },
                {
                    "type": "GSResourcePopupField",
                    "identifier": "model",
                    "attribute": "model",
                    "folder": "Live2D",
                    "bindings": [
                        { "source": "resource", "asDataFromFolder": "Live2D", "to": "model", "target": "global.data.model" },
                        { "source": "resource", "asDataFromFolder": "Live2D", "to": "positionView", "target": "model" }
                    ],
                    "delegates": [
                        { "identifiers": ["motion"], "selector": "update" }
                    ],
                    "frame": [1, 0]
                },
                {
                    "type": "GSTable",
                    "border": true,
                    "attribute": "motion",
                    "dataSource": {
                        "sourceFormula": "return d && d.global.model ? GS.VN.live2DMotionList(d.global.model) : []" 
                    },
                    "identifier": "motion",
                    "delegates": [{ "identifiers": ["positionView"], "selector": "setBackground:resource:"}],
                    "frame": [0, 1, -1, 200],
                    "bindings": [
                        { "source": "selectedItem", "to": "positionView", "target": "motion" }
                    ],
                    "columns": [
                        {
                            "identifier": "privilege",
                            "attribute": "privilege",
                            "text": "$$4870958C3ABB1243A2591832E3311D039368",
                            "width": 50,
                            "content": "return d"
                        }
                    ]
                }
            ]
        },
        {
            "name": "$$P117",
            "column": 1,
            "layout": { "type": "dynamicGrid", "spacing": [0, 0]},
            "items": [
                {
                    "type": "GSLive2DView",

                    "identifier": "positionView",
                    "attribute": "position",
                    "folder": "Live2D",
                    "frame": [0, 0],
                    "sizeFormula": "return GS.GAME.previewResolution();"
                }
            ]
        }
    ]
}
