{
    "parentId": "AC1ABDCBK050BA4863S9964E3DC378247DAD",
"name": "Position Selection",
    "windowTitle": "$$B30ED82745B4434C1A09FE025BC5866F2EE0",
    "type": "custom_view",
    "windowSize": {
        "w": 400, "h": 400
    },
    "bindings": [
        { "source": "local.data.picture", "postTranslator": "return GS.VN.imageForAnimation(p, d.originalSourceValue.animationId)", "asImageFromFolder": "Graphics/Pictures", "to": "positionView", "target": "cells.0" }
    ],
    "windowSizeFormula": "var res = GS.GAME.previewResolution(); return { w: res.w + GS.CONST.WINDOW_PADDING }",
    "sections": [
        {
            "name": "Position",
            "identifier": "positionSection",
            "noBorder": true,
            "column": 0,
            "items": [
                {
                    "type": "GSLabel",
                    "text": "$$2983646A2E7A344B6429830460A50DA4FB4A",
                    "frame": [0, 0],
                    "group": "labels"
                },
                {
                    "type": "GSResourcePopupField",
                    "folder": "Graphics/Backgrounds",
                    "defaultValue": { "name": "$live_preview_snapshot" },
                    "bindings": [{ "source": "image", "to": "positionView", "target": "backgrounds.0" }],
                    "identifier": "resource",
                    "attribute": "previewBackground",
                    "frame": [1, 0]
                },
                {
                    "type": "GSDispositionView",
                    "border": true,
                    "identifier": "positionView",
                    "storeMode": "single",
                    "attribute": "position",
                    "folder": "Graphics/Characters",
                    "cellPropertiesDialog": { "uid": "7C0818997DE4D742260BAF889CD17188005B" },
                    "cellCount": 1,
                    "scaleForPreview": true,
                    "frame": [0, 2],
                    "sizeFormula": "return GS.GAME.previewResolution();"
                }
            ]
        }
    ]
}
