    "type": "custom_view",
    "id": "gs.SceneViewMassEdit",
    "windowSize": {
        "w": 600
    },
    "sections": [
        {
            "name": "general",
            "noBorder": true,
            "layout": {
                "type": "dynamicGrid",
                "fixedRowHeight": true
            },
            "column": 0,
            "items": [
                {
                    "type": "GSLabel",
                    "text": "$$66CE9EBB3C7AC34F94681F0614829EE08D8B",
                    "toolTip": "$$D086F69CK2E31A4A73S96F3E73722D94043A",
                    "group": "other",
                    "frame": [
                        0,
                        0
                    ]
                },
                {
                    "type": "GSComboBox",
                    "identifier": "type",
                    "attribute": "command",
                    "aliasKey": "alias.id",
                    "dataSource": [
                        { "name": "Wait", "alias": { "id": "gs.WaitCommand", "dataSource": [
                            { "name": "Duration", "alias": { "name": "Duration", "attribute": "time", "type": "number" } }
                        ] } }
                    ],
                    "bindings":[
                        { "source": "dataSource", "to": "numberField", "target": "dataSource" },
                        { "source": "dataSource", "to": "textField", "target": "dataSource" },
                        { "source": "dataSource", "to": "type", "target": "global.data.dataSource" }
                    ],
                    "defaultValue": "gs.WaitCommand",
                    "frame": [
                        1,
                        0,
                        -1,
                        -1
                    ]
                },
                {
                    "type": "GSRadioButton",
                    "identifier": "rbNumberField",
                    "defaultValue": true,
                    "attribute": "fieldType",
                    "value": 0,
                    "text": "$$28CEE33968FFD547223897A19718551FC5FC",
                    "toolTip": "$$3E003AE1K5374A4117SBE07E938FEE1C54D3",
                    "enableItems": ["numberField", "numberOperation", "numberValue"],
                    "group": "labels",
                    "frame": [0, 1]
                },
                {
                    "type": "GSComboBox",
                    "identifier": "numberField",
                    "attribute": "field",
                    "dataSource": { "source": [], "filter": "return d.alias.type == 'number'" } ,
                    "defaultValue": 0,
                    "frame": [1, 1]
                },
                {
                    "type": "GSComboBox",
                    "identifier": "numberOperation",
                    "attribute": "operation",
                    "allowEmptySelection": false,
                    "dataSource": ["$$98C1B05DKFBC9A48D8SA91FED96D78BF7B5D", "$$1FDFC337K71F5A4193S8B3CEDC546CFAA990", "$$00326F7FK4552A4D22SABC3EE8415B7304EC"],
                    "defaultValue": 0,
                    "frame": [2, 1]
                },
                {
                    "type": "GSStepper",
                    "identifier": "numberValue",
                    "attribute": "value",
                    "frame": [3, 1]
                },
                {
                    "type": "GSRadioButton",
                    "attribute": "fieldType",
                    "value": 1,
                    "identifier": "rbTextField",
                    "enableItems": ["textField", "textOperation", "textValue"],
                    "text": "$$C3FE01727AC94746117A85B98A18964B68DF",
                    "toolTip": "$$3E003AE1K5374A4117SBE07E938FEE1C54D3",
                    "group": "labels",
                    "frame": [0, 2]
                },
                {
                    "type": "GSComboBox",
                    "identifier": "textField",
                    "attribute": "field",
                    "allowEmptySelection": false,
                    "dataSource": { "source": [], "filter": "return d.alias.type == 'text'" } ,
                    "defaultValue": 0,
                    "frame": [1, 2]
                },
                {
                    "type": "GSComboBox",
                    "attribute": "operation",
                    "identifier": "textOperation",
                    "allowEmptySelection": false,
                    "dataSource": ["$$98C1B05DKFBC9A48D8SA91FED96D78BF7B5D", "$$88DC6827KEC9CA46E8S92E8EFCB66507A6F9", "$$P202"],
                    "defaultValue": 0,
                    "frame": [2, 2]
                },
                {
                    "type": "GSTextField",
                    "identifier": "textValue",
                    "attribute": "value",
                    "frame": [3, 2]
                },
                {
                    "type": "GSRadioButton",
                    "attribute": "fieldType",
                    "value": 2,
                    "identifier": "rbScript",
                    "enableItems": [],
                    "text": "$$48C87A8F4A92D44CCE1B50A5DC5E3E782944",
                    "toolTip": "$$3E003AE1K5374A4117SBE07E938FEE1C54D3",
                    "group": "labels",
                    "enableItems": ["script"],
                    "frame": [0, 3]
                },
                {
                    "type": "GSTextField",
                    "multiline": true,
                    "attribute": "script",
                    "identifier": "script",
                    "frame": [0, 4, -1, 150]
                },
                {
                    "type": "GSTable",
                    "attribute": "conditions",
                    "identifier": "conditions",
                    "border": true,
                    "emptyItem": true,
                    "dialog": { "uid": "17CDE388KEE10A4BF8S8F45E0228F4E64A01" },
                    "contextMenu": [
                        {
                            "name": "$$E9E7B6C664300341618952F967AA397BC063", "selector": "addItemUsingDialog"
                        },
                        {
                            "separator": true
                        },
                        {
                            "name": "$$P49", "selector": "deleteItem:" 
                        }
                    ],
                    "columns": [
                        {
                            "identifier": "name",
                            "attribute": "name",
                            "text": "$$CA9291341741E846750926C538C5BE570B87",
                            "width": 50,
                            "content": "return fmt('[%s] %s [%s]', d.field.name, GS.CONST.COMPARE_OPERATIONS[d.operation], d.value)"
                        }
                    ],
                    "frame": [0, 5, -1, 150]
                }


            ]
        }
    ]
}
