class DesktopUIConstants
    @OPTION_BUTTON_W = 78
    @OPTION_BUTTON_H = 78
    @OPTION_BUTTON_L_IMAGE_ON = "m-diamond-large-on"
    @OPTION_BUTTON_L_IMAGE_OFF = "m-diamond-large-off"
    @OPTION_BUTTON_S_IMAGE_ON = "m-diamond-small-on"
    @OPTION_BUTTON_S_IMAGE_OFF = "m-diamond-small-off"
    @OPTION_BUTTON_MSG_IMAGE_ON = "msg-diamond-small-on"
    @OPTION_BUTTON_MSG_IMAGE_OFF = "msg-diamond-small-off"
    @SLIDER_TRACK_H = 3
    @TEXT_SIZE_SMALL = 20
    @TEXT_SIZE_MESSAGE = 30
    @TEXT_SIZE_MESSAGE_NAME = 25
    @LAYOUT_SETTINGS_WINDOW_X = 20
    @LAYOUT_SETTINGS_WINDOW_W = 630
    @LAYOUT_SETTINGS_VOICES_WINDOW_X = 720
    @LAYOUT_SETTINGS_VOICES_WINDOW_W = 540
    @LAYOUT_SETTINGS_WINDOW_LABEL_W = 80
    @CG_GALLERY_CONTENT_WIDTH = Math.floor((Graphics.width-260-(Graphics.width - (Graphics.width - 200))) / 175) * 175 + 20
    @MESSAGE_BOX_IDS = ["messageBox", "messageBoxNVL"]

class Helper
    ###*
    * A helper class containing helper-functions like generating letter-descriptors
    * from database defined text-input pages.
    *
    * @module ui
    * @class Helper
    * @memberof ui
    * @constructor
    ###
    constructor: ->

    ###*
    * Generates the text-input pages from database.
    *
    * @method generateTextInputPages
    * @return Object[] - Array of text-input pages. Each page is an array of ui.Letter descriptors.
    ###
    generateTextInputPages: () ->
        pages = []
        defaults = [
            "ABCDEFGHIJKLMNOPQRSTUVWXYZ", "abcdefghijklmnopqrstuvwxyz",
            "0123456789", ""
        ]

        try
            charsets = RecordManager.system.textInputPages.select (p) -> lcs(p)
        catch
            charsets = defaults

        for i in [0...charsets.length]
            charsets[i] = charsets[i] ? defaults[i]||""

        for charset in charsets
            pages.push(@generateTextInputPage(charset))

        return pages

    ###*
    * Generates an array of ui.Letter descriptors from the specified charset.
    *
    * @method generateTextInputPage
    * @param {string} charset - The charset to generate the ui.Letter descriptors from.
    * @return Object[] - Array of ui.Letter descriptors.
    ###      
    generateTextInputPage: (charset) ->
        controls = []

        for c in charset
            letter = { "type": "ui.Letter", "params": { "text": c, "target": new ui.Formula(-> 'textField.textInput') } }
            controls.push(letter)

        return controls

ui.Helper = new Helper()  
gs.DesktopUIConstants = DesktopUIConstants
gs.UIConstants = DesktopUIConstants

