{
    "windowTitle": "Run Configuration",
    "type": "custom_view",
    "windowSize": {
        "w": 400
    },
    "sections": [
        {
            "name": "",
            "noBorder": true,
            "items": [
                {
                    "type": "GSLabel",
                    "text": "Name:",
                    "group": "labels",
                    "frame": [0, 0]
                },
                {
                    "type": "GSTextField",
                    "attribute": "name",
                    "frame": [1, 0]
                },
                {
                    "type": "GSLabel",
                    "text": "Build:",
                    "group": "labels",
                    "frame": [0, 1]
                },
                {
                    "type": "GSComboBox",
                    "dataSource": { 
                        "source": "$appPreferences.build.configurations",
                        "numberingFormat": ""
                    },
                    "attribute": "buildConfiguration",
                    "frame": [1, 1]
                },
                {
                    "type": "GSLabel",
                    "text": "Run Phases",
                    "frame": [0, 2]
                },
                {
                    "type": "GSTable",
                    "attribute": "phases",
                    "identifier": "phases",
                    "emptyItem": true,
                    "border": true,
                    "dialog": {
                        "uid": "82487C7C299F124DAB2B7365A07322AB3DDD"
                    },
                    "contextMenu": GS.UI.CREATE_TABLE_CONTEXT_MENU("Add Phase"),
                    "columns": [
                        {
                            "identifier": "name",
                            "attribute": "name",
                            "text": "Name",
                            "width": 125,
                            "content": "return d.name"
                        },
                        {
                            "identifier": "commandLine",
                            "attribute": "commandLine",
                            "text": "Command Line",
                            "width": 50,
                            "content": "return d.commandLine"
                        }
                    ],
                    "frame": [0, 3, -1, 150]
                }
            ]
        }
    ]
}
