{
    "parentId": "AC1ABDCBK050BA4863S9964E3DC378247DAD",
"name": "Live2D Position Selection",
    "windowTitle": "Select Position",
    "type": "custom_view",
    "windowSize": {
        "w": 400, "h": 400
    },
    "bindings": [
        { "source": "local.data.resource", "asDataFromFolder": "Live2D", "to": "positionView", "target": "model" }
    ],
    "windowSizeFormula": "var res = GS.GAME.previewResolution(); return { w: res.w +  + GS.CONST.WINDOW_PADDING }",
    "sections": [
        {
            "name": "Position",
            "identifier": "positionSection",
            "noBorder": true,
            "column": 0,
            "items": [
                {
                    "type": "GSLabel",
                    "text": "$$2983646A2E7A344B6429830460A50DA4FB4A",
                    "frame": [0, 0],
                    "group": "labels"
                },
                {
                    "type": "GSResourcePopupField",
                    "folder": "Graphics/Backgrounds",
                    "identifier": "resource",
                    "attribute": "previewBackground",
                    "bindings": [{ "source": "image", "to": "positionView", "target": "backgrounds.0" }],
                    "frame": [1, 0]
                },
                {
                    "type": "GSLabel",
                    "text": "$$FBD21B3DK57FEA4AA4SBF2EEA70C92043612",
                    "frame": [0, 1],
                    "group": "labels"
                },
                {
                    "type": "GSStepper",
                    "minimum": 10,
                    "maximum": 1000,
                    "defaultValue": 100,
                    "valueTranslator": { "read": "return Math.round(v * 100);", "write": "return v / 100;" },
                    "attribute": "position.zoom.d",
                    "identifier": "zoom",
                    "bindings": [
                         { "source": "value", "to": "positionView", "target": "objectZoom" }
                    ],
                    "frame": [1, 1]
                },
                {
                    "type": "GSLive2DView",
                    "border": true,
                    "identifier": "positionView",
                    "attribute": "position",
                    "folder": "Live2D",
                    "frame": [0, 2],
                    "sizeFormula": "return GS.GAME.previewResolution();",
                    "bindings": [
                        { "source": "zoom", "to": "zoom", "target": "value" }
                    ]
                }
            ]
        }
    ]
}
