ui.UiFactory.customTypes["ui.StepSlider"] = {
    "type": "ui.FreeLayout",
    "formulas": [
        $ -> if @onInitialize then o.controls[2].draggable.step = o.params.read.exec()
        $ -> if @onChange o.controls[2].draggable.step then o.params.write.exec(o.controls[2].draggable.step)
    ],
    "controls": [
        {
            "type": "ui.Panel",
            "style": "sliderTrack",
            "frame": [0, 0, "100% - 90", gs.UIConstants.SLIDER_TRACK_H],
            "alignmentY": "center"
        },
        {
            "type": "ui.SpreadLayout",
            "frame": [0, 0, "100% - 90", "100%"],
            "dataField": -> p.data,
            "template": {
                "type": "ui.Image",
                "image": gs.UIConstants.OPTION_BUTTON_S_IMAGE_OFF,
                "alignmentY": "center"
            }
        },
        {
            "style": "sliderKnob",
            "focusable": true,
            "imageHandling": 0,
            "type": "ui.Image",
            "frame": [0, 0],
            "alignmentY": "center",
            "formulas": [
                $ -> o.draggable.rect.width = o.parent.controls[0].dstRect.width
                $ -> o.parent.controls[3].text = o.parent.params.data[o.parent.controls[2].draggable.step]
            ],
            "draggable": { "rect": [0, 0, 100, gs.UIConstants.OPTION_BUTTON_H], "axisX": true, "axisY": false, "steps": -> p.steps }
        },
        {
            "type": "ui.Text",
            "color": [255, 255, 255],
            "frame": [0, 0],
            "style": "smallUIText",
            "alignmentX": "right",
            "alignmentY": "center",
            "text": "Hello",
            "sizeToFit": true
        }
    ]
}

