  "type": "ui.FreeLayout",
  "formulas": [
    $(function() {
      if (this.onInitialize) {
        return o.controls[2].draggable.step = o.params.read.exec();
      }
    }), $(function() {
      if (this.onChange(o.controls[2].draggable.step)) {
        return o.params.write.exec(o.controls[2].draggable.step);
      }
    })
  ],
  "controls": [
    {
      "type": "ui.Panel",
      "style": "sliderTrack",
