      "style": "sliderKnob",
      "focusable": true,
      "imageHandling": 0,
      "type": "ui.Image",
      "frame": [0, 0],
      "alignmentY": "center",
      "formulas": [
        $(function() {
          return o.draggable.rect.width = o.parent.controls[0].dstRect.width;
        }), $(function() {
          return o.parent.controls[3].text = o.parent.params.data[o.parent.controls[2].draggable.step];
        })
      ],
      "draggable": {
        "rect": [0, 0, 100, gs.UIConstants.OPTION_BUTTON_H],
        "axisX": true,
        "axisY": false,
        "steps": function() {
          return p.steps;
        }
      }
