{
    "parentId": "AC1ABDCBK050BA4863S9964E3DC378247DAD",
"name": "Move Character Position",
    "windowTitle": "$$B30ED82745B4434C1A09FE025BC5866F2EE0",
    "type": "custom_view",
    "windowSize": {
        "w": 400, "h": 400
    },
    "bindings": [
        { "source": "local.data.expressionId", "to": "expression", "target": "index" },
        { "source": "local.data.expressionId", "translator": "return GS.VN.resourceForExpression(p)", "asImageFromFolder": "Graphics/Characters",  "to": "positionView", "target": "cells.0" }
    ],
    "windowSizeFormula": "var res = GS.GAME.previewResolution(); return { w: res.w + GS.CONST.WINDOW_PADDING }",
    "sections": [
        {
            "name": "Position",
            "identifier": "positionSection",
            "noBorder": true,
            "column": 0,
            "items": [
                {
                    "type": "GSLabel",
                    "text": "$$2DD5576B314560493B5ACDB32E6C8F750939",
                    "frame": [0, 0],
                    "group": "labels"
                },
                {
                    "type": "GSResourcePopupField",
                    "folder": "Graphics/Backgrounds",
                    "defaultValue": { "name": "$live_preview_snapshot" },
                    "identifier": "resource",
                    "attribute": "previewBackground",
                    "bindings": [{ "source": "image", "to": "positionView", "target": "backgrounds.0" }],
                    "frame": [1, 0]
                },
                {
                    "type": "GSLabel",
                    "text": "$$016A6CB83417154EFB1BAB377B4229A39BAC",
                    "frame": [0, 1],
                    "group": "labels"
                },
                {
                    "type": "GSDataRecordField",
                    "dataSource": "characterExpressions",
                    "identifier": "expression",
                    "bindings": [
                        { "source": "idle.0.resource", "asImageFromFolder": "Graphics/Characters", "to": "positionView", "target": "cells.0" }
                    ],
                    "frame": [1, 1]
                },
                {
                    "type": "GSDispositionView",
                    "border": true,
                    "identifier": "positionView",
                    "storeMode": "single",
                    "attribute": "position",
                    "cellPropertiesDialog": { "uid": "7C0818997DE4D742260BAF889CD17188005B" },
                    "folder": "Graphics/Characters",
                    "cellCount": 1,
                    "scaleForPreview": true,
                    "frame": [0, 2],
                    "sizeFormula": "return GS.GAME.previewResolution();"
                }
            ]
        }
    ]
}
