{
    "parentId": "AC1ABDCBK050BA4863S9964E3DC378247DAD",
"name": "Image Map Hotspot Selection",
    "windowTitle": "$$AF0EA7FD53E54248A25AEAE9D653D48AE7D1",
    "type": "custom_view",
    "windowSize": {
        "w": 400, "h": 400
    },
    "windowSizeFormula": "var res = GS.GAME.previewResolution(); return { w: res.w + GS.CONST.WINDOW_PADDING }",
    "bindings": [
        { "source": "local.data.ground", "asImageFromFolder": "Graphics/Pictures", "to": "positionView", "target": "backgrounds.0" },
        { "source": "local.data.unselected", "asImageFromFolder": "Graphics/Pictures", "to": "positionView", "target": "backgrounds.1" }
    ],
    "sections": [
        {
            "name": "Position",
            "identifier": "positionSection",
            "noBorder": true,
            "column": 0,
            "items": [
                {
                    "type": "GSDispositionView",
                    "border": true,
                    "dynamic": true,
                    "cellPropertiesDialog": { "uid": "50F271DFKF1A8A4EF7S8963EFCD47E4251B0" },
                    "identifier": "positionView",
                    "attribute": "hotspots",
                    "folder": "Graphics/Characters",
                    "allowCellResize": true,
                    "allowCellCopy": true,
                    "scaleForPreview": true,
                    "frame": [0, 0],
                    "sizeFormula": "return GS.GAME.previewResolution();"
                },
                {
                    "type": "GSButton",
                    "text": "$$DA207B80909C2343435A95E4112CEB0C7DD9",
                    "identifier": "add",
                    "data": { "size": { "width": 100, "height": 100 }, "color": [0, 0, 0, 160] },
                    "delegates": [
                        { "identifiers": ["positionView"], "selector":"addCell:cell:" }
                    ],
                    "frame": [0, 1]
                },
                {
                    "type": "GSButton",
                    "identifier": "remove",
                    "text": "$$F12DA9CB7F97A94C6E4BD5188F899805C688",
                    "delegates": [
                        { "identifiers": ["positionView"], "selector":"removeSelectedCell:" }
                    ],
                    "frame": [1, 1]
                }
            ]
        }
    ]
}
