        easingY: @easingY,
        range: @range,
        duration: @duration,
        speed: @speed,
        isRunning: @isRunning

    ###*
    * Skips the animation. That is used to skip an animation if the user
    * wants to skip very fast through a visual novel scene.
    *
    * @method skip
    ###   
    skip: -> 
        if @easingX.duration > 1
            @easingX.duration = 1
            @easingX.time = 0
        if @easingY.duration > 1
            @easingY.duration = 1
            @easingY.time = 0

    ###*
    * Starts the shake-animation.
    *
    * @method start
    * @param {gs.Range} range The horizontal shake-range.
    * @param {number} speed The shake-speed.
    * @param {number} duration The duration in frames.
    * @param {Object} easingType The easing-type.
    * @param {function} [callback] An optional callback called if the animation is finished. 
    ###      
    start: (range, speed, duration, easing, callback) ->
        @range = range
        @easingX.type = easing
        @easingY.type = easing
        @speed = speed
        @duration = duration
        @callback = callback

        if @duration == 0 or @isInstantSkip()
            @callback?(@object, this)
        else
            @isRunning = yes
            @startOffset = x: @object.offset.x, y: @object.offset.y
            if range.x != 0
                @easingX.startValue(@startOffset.x, range.x, Math.max(range.x / speed, 1))
            if range.y != 0
                @easingY.startValue(@startOffset.y, range.y, Math.max(range.y / speed, 1))

