    * Updates the shake-animation.
    *
    * @method update
    ### 
    update: ->
        super()

        if (@duration == 0 or @isInstantSkip()) and @isRunning
            @easingX.isRunning = no
            @easingY.isRunning = no
            @object.offset.x = 0
            @object.offset.y = 0
            @isRunning = no
            @callback?(@object, this)

        if not @isRunning then return

        @easingX.updateValue()
        @easingY.updateValue()

        if @easingY.isRunning
            @object.offset.y = Math.round(@easingY.value)
        if @easingX.isRunning
            @object.offset.x = Math.round(@easingX.value)

        if not @easingX.isRunning
            @easingX.startValue(@object.offset.x, (if @object.offset.x > 0 then -(@range.x*2) else (@range.x*2)), Math.max(@range.x*2 / @speed, 2))
        if not @easingY.isRunning
            @easingY.startValue(@object.offset.y, (if @object.offset.y > 0 then -(@range.y*2) else (@range.y*2)), Math.max(@range.y*2 / @speed, 2))


        @duration--

