{
    "parentId": "AC1ABDCBK050BA4863S9964E3DC378247DAD",
"name": "Live2D Motion Group Selection",
    "windowTitle": "$$93B9D3A6536FB14D0F68D9A142B99F56D8C7",
    "type": "custom_view",
    "windowSize": {
        "w": 800, "h": 400
    },
    "bindings": [
        { "source": "local.data.resource", "asDataFromFolder": "Live2D", "to": "positionView", "target": "model" }
    ],
    "windowSizeFormula": "var res = GS.GAME.previewResolution(); return { w: res.w + 385  + GS.CONST.WINDOW_PADDING }",
    "columns": [400, -1],
    "sections": [
        {
            "name": "Position",
            "identifier": "positionSection",
            "noBorder": true,
            "column": 0,
            "items": [
                {
                    "type": "GSLabel",
                    "text": "$$79C3606A31A6A347B80AD628C0099CFE1421",
                    "frame": [0, 0],
                    "group": "labels"
                },
                {
                    "type": "GSResourcePopupField",
                    "identifier": "model",
                    "attribute": "model",
                    "folder": "Live2D",
                    "bindings": [
                        { "source": "resource", "asDataFromFolder": "Live2D", "to": "model", "target": "global.data.model" },
                        { "source": "resource", "asDataFromFolder": "Live2D", "to": "positionView", "target": "model" }
                    ],
                    "delegates": [
                        { "identifiers": ["motion"], "selector": "update" }
                    ],
                    "frame": [1, 0]
                },
                {
                    "type": "GSTable",
                    "border": true,
                    "attribute": "motionGroup",
                    "dataSource": {
                        "sourceFormula": "return d && d.global.model ? GS.VN.live2DMotionGroupList(d.global.model) : []" 
                    },
                    "identifier": "motion",
                    "delegates": [{ "identifiers": ["positionView"], "selector": "setBackground:resource:"}],
                    "frame": [0, 1, -1, 200],
                    "bindings": [
                        { "source": "selectedItem", "to": "positionView", "target": "motionGroup" }
                    ],
                    "columns": [
                        {
                            "identifier": "privilege",
                            "attribute": "privilege",
                            "text": "$$A1670E332C64B44EA22BDCE357DA4542221E",
                            "width": 50,
                            "content": "return d"
                        }
                    ]
                },
                {
                    "type": "GSRadioButton",
                    "attribute": "playType",
                    "identifier": "ordered",
                    "value": 0,
                    "text": "$$C167CCAE6B95034BD0686DE82A7E61ABE04F",
                    "frame": [0, 2],
                    "group": "playType",
                    "bindings": [
                        { "source": "value", "to": "positionView", "target": "motionGroupPlayType" }
                    ]
                },
                {
                    "type": "GSRadioButton",
                    "identifier": "random",
                    "defaultValue": true,
                    "attribute": "playType",
                    "value": 1,
                    "text": "$$AF03E39FK6E5DA4AC1SA59CEE656A02E67B8",
                    "frame": [1, 2],
                    "group": "playType",
                    "bindings": [
                        { "source": "value", "to": "positionView", "target": "motionGroupPlayType" }
                    ]
                }
            ]
        },
        {
            "name": "$$P117",
            "column": 1,
            "layout": { "type": "dynamicGrid", "spacing": [0, 0]},
            "items": [
                {
                    "type": "GSLive2DView",
                    "identifier": "positionView",
                    "attribute": "position",
                    "folder": "Live2D",
                    "frame": [0, 0],
                    "sizeFormula": "return GS.GAME.previewResolution();"
                }
            ]
        }
    ]
}
