    toDataBundle: ->
        easing: @easing,
        animationType: @animationType

    ###*
    * Updates the mask-animation.
    *
    * @method update
    ###    
    update: ->
        super()
        if not @easing.isRunning then return

        @easing.updateValue()
        @object.mask.value = @easing.value

        if not @easing.isRunning
            if @animationType < 1
                @object.mask.source = null
                @object.mask.vague = 0
            @animationType = 0
            @callback?(@object, this)

    ###*
    * Lets the game object appear on screen using a masking-effect.
    *
    * @method maskIn
    * @param {gs.Mask} mask The mask used for the animation.
    * @param {number} duration The duration in frames.
    * @param {Object} easingType The easing-type.
    * @param {function} [callback] An optional callback-function called when the animation is finished. 
    ###       
    maskIn: (mask, duration, easing, callback) ->
        @easing.type = easing
        @animationType = 0
        @callback = callback

        if duration == 0 or @isInstantSkip()
            @callback?(@object, this)
        else
            @object.mask.type = 1
            @object.mask.source = ResourceManager.getBitmap("Graphics/Masks/#{mask.graphic?.name}")
            @object.mask.vague = mask.vague
            @object.mask.value = 255

            @easing.startValue(255, -255 - mask.vague, duration)

    ###*
    * Lets the game object disappear from screen using a masking-effect.
    *
    * @method maskIn
    * @param {gs.Mask} mask The mask used for the animation.
    * @param {number} duration The duration in frames.
    * @param {Object} easingType The easing-type.
    * @param {function} [callback] An optional callback-function called when the animation is finished. 
    ###      
    maskOut: (mask, duration, easing, callback) ->
        @easing.type = easing
        @animationType = 0
        @callback = callback

        if duration == 0 or @isInstantSkip()
            @callback?(@object, this)
        else
            @object.mask.type = 1
            @object.mask.source = ResourceManager.getBitmap("Graphics/Masks/#{mask.graphic?.name}")
            @object.mask.vague = mask.vague
            @object.mask.value = 0

            @easing.startValue(-mask.vague, 255 + mask.vague, duration)

    ###*
    * Description follows...
    *
    * @method maskTo
    * @param {gs.Mask} mask The mask used for the animation.
    * @param {number} duration The duration in frames.
    * @param {Object} easingType The easing-type.
    * @param {function} [callback] An optional callback-function called when the animation is finished. 
    ###       
    maskTo: (mask, duration, easing, callback) ->
        @easing.type = easing
        @animationType = 1
        @callback = callback

        @object.mask.type = 1

        # FIXME: Doubled code, see CommandInterpreter.commandMaskPicture
        if mask.sourceType == 0
            @object.mask.source = ResourceManager.getBitmap("Graphics/Masks/#{mask.graphic?.name}")
        else
            if @object.mask.source?.videoElement?
                @object.mask.source.pause()

            @object.mask.source = ResourceManager.getVideo("Movies/#{mask.video.name}")
            @object.mask.source?.play()
            @object.mask.source?.loop = true

        @object.mask.vague = mask.vague

        if duration == 0 or @isInstantSkip()
            @animationType = 0
            @object.mask.value = mask.value
            @callback?(@object, this)
        else
            @easing.startValue(@object.mask.value, mask.value - @object.mask.value, duration)

